using UnityEngine; using System.Collections;  public class UFOController : MonoBehaviour {  	private Rigidbody2D rigidbod2d = null;   	public ScoreManager scoreManager;  	// Use this for initialization 	void Start () {  		rigidbod2d = this.GetComponent<Rigidbody2D> ();  	}  	public float forceValue;  	// Update is called once per frame 	void Update () {  		if(Input.GetKey(KeyCode.W)){ 			rigidbod2d.AddForce (new Vector3(0,forceValue,0)); 		}  		if(Input.GetKey(KeyCode.S)){ 			rigidbod2d.AddForce (new Vector3(0,-forceValue,0)); 		}  		if(Input.GetKey(KeyCode.A)){ 			rigidbod2d.AddForce (new Vector3(-forceValue,0,0)); 		}  		if(Input.GetKey(KeyCode.D)){ 			rigidbod2d.AddForce (new Vector3(forceValue,0,0)); 		} 	}  	public void OnTriggerEnter2D(Collider2D other) 	{ 		other.gameObject.SetActive (false); 		scoreManager.AddScore (100); 	} }